log("Creating JAR file: " + toFolder + adapterName + ".jar");
try {
	_zipper.zip(toFolder + adapterName + ".jar", files);
} catch (ex) {
	errors.push(ex);
}

defaultLogLevel = "INFO";
function logmsg(lvl, msg) {
	var lvl = (msg) ? lvl : defaultLogLevel;
	var msg = (msg) ? msg : lvl;
	task.logmsg(lvl, msg);
}

