padding = 1;
function pad(len) {
	return "\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t".substring(0, len);
}

function toJS(obj, lvl) {
	lvl = lvl || 0;
	var js = new java.lang.StringBuffer();
	var props = [];
	for (var pname in obj) {
		var pobj = obj[pname];
		if (pobj instanceof Array) {
			var arr = [];
			for (var i = 0; i < pobj.length; i++) {
				thisObj = pobj[i];
				arr.push(
						"\n" + pad(lvl*padding) + "{"
						+ toJS(thisObj, lvl+1)
						+ "\n" + pad(lvl*padding) + "}"
						);
			}
			props.push(
					"\n" + pad(lvl*padding) 
					+ pname + ": ["
					+ arr.join(",")
					+ "\n" + pad(lvl*padding) + "]"
					);
		} else
		if (pobj instanceof Object) {
			props.push(
					"\n" + pad(lvl*padding) 
					+ pname + ": {"
					+ toJS(pobj, lvl+1)
					+ "\n" + pad(lvl*padding) + "}"
					)
		} else 
		if (typeof pobj == "string") {
			props.push( 
					"\n" + pad(lvl*padding) 
					+ pname + ": "
					+ '"' + pobj + '"'
					);
		} else {
			props.push(
					"\n" + pad(lvl*padding) 
					+ pname + ": "
					+ pobj
					);
		}
	}
	js.append(props.join(","));

	return js;
}

