	} catch (ex) {
		msg = "Error opening Config xml file " + inFileName + " - " + ex;
		errors.push(msg);
	}	

/* Not writing anymore
	try {
		outFileName = toFolder + thisAL + ".xml";
		toFile = system.openFileForOutput(toFolder + thisAL + ".xml");
	} catch (ex) {
		msg = "Error creating output file " + outFileName + " - " + ex;
		errors.push(msg);
	}
