			fromFile.close();
		} catch (ex) {
			// do nothing
		}
		try {
//			toFile.close();
		} catch (ex) {
			// do nothing
		}

		return;
	}

	lines = [];
	try {
		do {
			line = fromFile.readLine();
			if (line != null) {
				lines.push(line);
//				toFile.write(line);
//				toFile.newLine();
			}
		} while (line != null);
	} catch (ex) {
		msg = "Error writing AL file " + toFile + " - " + ex;
		errors.push(msg);
	}

	fromFile.close();
//	toFile.close();
	files[outFileName] = lines.join("\n");
}

