	msg = "You must define attribute.* properties in the AdapterKit.properties file";
	log("ERROR", msg);
	errors.push(msg);
}
if (count.classes < 2) {
	msg = "You must define class.* properties for both the User/Accout object and the Service itself in the AdapterKit.properties file";
	log("ERROR", msg);
	errors.push(msg);
}

