                    mutability: "readWrite",
                    returned: "default",
                    uniqueness: "none"
                },
                {
                    name: "erpassword",
                    type: "string",
                    multiValued: false,
                    description: "A password used to authenticate a user.",
                    required: false,
                    caseExact: true,
                    mutability: "writeOnly",
                    returned: "never",
                    uniqueness: "none"
                },
                {
